import React, { useState } from "react";
import { Box, TextField, Button, Typography } from "@mui/material";
import axios from "axios";

function ChatInterface() {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState("");

  const sendMessage = async () => {
    if (userInput.trim() === "") return;
    const userMessage = { sender: "user", text: userInput, timestamp: new Date() };
    setMessages((prev) => [...prev, userMessage]);

    try {
      const response = await axios.post("/chat", { message: userInput });
      const aiMessage = { sender: "ai", text: response.data.response, timestamp: new Date() };
      setMessages((prev) => [...prev, aiMessage]);
    } catch (error) {
      console.error("Error sending chat message:", error);
    }

    setUserInput("");
  };

  return (
    <Box
      display="flex"
      flexDirection="column"
      height="100vh"
      maxHeight="100vh"
    >
      {/* Chat Messages */}
      <Box
        flexGrow={1}
        overflow="auto"
        p={2}
        sx={{ backgroundColor: "#F5F5F5", borderRadius: "4px" }}
      >
        {messages.map((msg, index) => (
          <Box
            key={index}
            display="flex"
            justifyContent={msg.sender === "user" ? "flex-end" : "flex-start"}
            mb={1}
          >
            <Box
              bgcolor={msg.sender === "user" ? "#A8D5E2" : "#F4D9C6"}
              p={2}
              borderRadius={4}
              maxWidth="60%"
            >
              <Typography variant="body1">{msg.text}</Typography>
              <Typography variant="caption" display="block" textAlign="right">
                {new Date(msg.timestamp).toLocaleTimeString()}
              </Typography>
            </Box>
          </Box>
        ))}
      </Box>

      {/* Input Box */}
      <Box
        display="flex"
        p={2}
        borderTop="1px solid #ddd"
        sx={{ backgroundColor: "#FFFFFF" }}
      >
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Type your message..."
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          onKeyPress={(e) => e.key === "Enter" && sendMessage()}
        />
        <Button
          variant="contained"
          color="primary"
          onClick={sendMessage}
          sx={{ ml: 2 }}
        >
          Send
        </Button>
      </Box>
    </Box>
  );
}

export default ChatInterface;
