import axios from "axios";

// Base API URL
const BASE_URL = "http://127.0.0.1:5000";

// Fetch diagnosis results
export const fetchDiagnosis = async () => {
  try {
    const response = await axios.get(`${BASE_URL}/diagnose`);
    return response.data;
  } catch (error) {
    console.error("Error fetching diagnosis:", error);
    throw error;
  }
};

// Fetch trend chart data
export const fetchTrendChart = async () => {
  try {
    const response = await axios.get(`${BASE_URL}/trend_chart`);
    return response.data; // Ensure backend provides chart data as JSON
  } catch (error) {
    console.error("Error fetching trend chart:", error);
    throw error;
  }
};

// Send chat message
export const sendChatMessage = async (message) => {
    try {
      const response = await axios.post(
        `${BASE_URL}/chat`,
        { message },
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      return response.data;
    } catch (error) {
      console.error("Error in API call:", error);
      throw error;
    }
  };
