import React, { useState } from "react";
import { Box, TextField, Button, Typography } from "@mui/material";
import axios from "axios";

function ChatInterface() {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState("");

  const sendMessage = async () => {
    if (userInput.trim() === "") return;

    const userMessage = { sender: "user", text: userInput, timestamp: new Date() };
    setMessages((prev) => [...prev, userMessage]);

    try {
      const response = await axios.post("/chat", { message: userInput });
      const aiMessage = { sender: "ai", text: response.data.response, timestamp: new Date() };
      setMessages((prev) => [...prev, aiMessage]);
    } catch (error) {
      console.error("Error sending message:", error);
    }

    setUserInput("");
  };

  return (
    <Box
      display="flex"
      flexDirection="column"
      justifyContent="space-between"
      height="90vh"
      bgcolor="#f5f5f5"
    >
      {/* Chat Messages Area */}
      <Box
        flexGrow={1}
        overflow="auto"
        padding={2}
        sx={{ bgcolor: "#e8f5e9", borderTop: "1px solid #cfd8dc" }}
      >
        {messages.map((msg, index) => (
          <Box
            key={index}
            display="flex"
            justifyContent={msg.sender === "user" ? "flex-end" : "flex-start"}
            mb={2}
          >
            <Box
              bgcolor={msg.sender === "user" ? "#b3e5fc" : "#ffe0b2"}
              p={2}
              borderRadius={4}
              maxWidth="60%"
              boxShadow={1}
            >
              <Typography variant="body1">{msg.text}</Typography>
              <Typography
                variant="caption"
                display="block"
                textAlign={msg.sender === "user" ? "right" : "left"}
              >
                {new Date(msg.timestamp).toLocaleTimeString()}
              </Typography>
            </Box>
          </Box>
        ))}
      </Box>

      {/* Chat Input Area */}
      <Box display="flex" p={2} sx={{ bgcolor: "#ffffff", borderTop: "1px solid #cfd8dc" }}>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Type your message..."
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          onKeyPress={(e) => e.key === "Enter" && sendMessage()}
        />
        <Button
          variant="contained"
          color="primary"
          onClick={sendMessage}
          sx={{ ml: 2, height: "100%" }}
        >
          Send
        </Button>
      </Box>
    </Box>
  );
}

export default ChatInterface;
